import './app.scss';
import { Row, Container, Col } from "react-bootstrap";
import personal_photo from "./assets/personal_image.png";
import git_logo from "./assets/git_logo.png";
import linkedin_logo from "./assets/linkedin-logo.png";
import greads_logo from "./assets/greads-logo.png";
import spotify_logo from "./assets/spotify_logo.png";
import React from "react";

const icons = [
	{
		icon_image: git_logo,
		url: "https://github.com/luismomm2110",
		description: "github"
	},
	{
		icon_image: linkedin_logo,
		url: "https://www.linkedin.com/in/luis-antonio-momm-duarte-25224915a/",
		description: "linkedin"
	},
	{
		icon_image: spotify_logo, 
		url: "https://open.spotify.com/user/luismomm",
		description: "spotfy"
	},
	{
		icon_image: greads_logo, 
		url: "https://www.goodreads.com/user/show/64864252-luis-antonio-momm-duarte",
		description: "goodreads"
	} 
];

function Intro() { 
	return (
		<Container>
			<Row>
				<Col style={{"textAlign" : "left"}} lg={8}>
					Luis Antonio Momm Duarte 
				</Col>
				<Col style={{"textAlign" : "left", "outline" : "dotted", "float" : "left"}} lg={4}>
					luismomm@gmail.com
				</Col>
			</Row>
			<hr className="horizontal-line"></hr>
			<p className="main-text"> 
				Hi! I am a professional and technology student. I currently work at
				<a href="https://www.softexpert.com/company/"> SoftExpert </a>, 
				a brazilian company market leader in software for bussiness compliance, 
				where I work with data integration (mainly using pandas)
				and creation of SQL Scripts for BI panels for the company's customers.
			</p>
			<p className="main-text"> 
				My main professional interests are data, operating systems, functional programming
				and many other fields.
			</p>
		</Container>
	)
}

function Education() {
	return (
		<React.Fragment>
			<h4 className="header" style={{"fontSize" : "120%"}}>Education</h4>
			<hr className="horizontal-line"></hr>
			<p className="main-text"> 
				As an advocate of open-source education as democratization tool, 
				most of my technology education is through the  
				<a href="https://github.com/ossu/computer-science"> Open Source Society University (OSSU) curriculum for Computer Science</a> 
				 .Simultaneously, I study Software Engineering at Católica de Santa Catarina. 
			</p> 
			<p className="main-text">	
				Before that, I passed the admission exam for a Bachelor of Medicine at the Federal University of Santa Catarina, in which I got the nineteenth position out of more than 8000 candidates (top 0.3%). I also obtained a performance above 90% in the math test, achieved by only 23 candidates among more than 31000 (top 0.08%)
			</p>
			<h5 className="header" style={{"fontSize" : "80%"}}>Selected Projects</h5>
			<hr className="horizontal-line"></hr>
		</React.Fragment>
	)
}

function Photo() {
    return (
        <React.Fragment>
            <div className={"photo-box"}>
				<img className={"personal-photo"} alt="Hello, I'm Luis" src={personal_photo}></img>
            </div>
        </React.Fragment>
    )
}

function Icons({icons}) {
	return (
		<React.Fragment>
			{icons.map((icon) => (
				<a href={icon.url}><img className={"icon-image"} src={icon.icon_image} alt={icon.description} ></img></a>
			))
			}
			<br></br>
		</React.Fragment>
	)
}

function App() {
  return (
<Container className={"container-main"} fluid>  
			<Row> 
				<Col className={"side-box"} lg={3} xs={1}>
				</Col>
				<Col className={"center-box"} lg={6} xs={10}>
					<Photo />
					<Icons className={"icon-row"} icons={icons} />
					<Intro />
					<Education />
				</ Col> 
				<Col className={"side-box"} lg={3} xs={1}>
				</Col>
		</Row>
	</Container>
  );
}

export default App;
